import { scrollLinks } from '../../links';
import React, { useState, useEffect } from 'react';
import { FaBars, FaTimes } from 'react-icons/fa';
import { DiCssdeck } from 'react-icons/di';
import { Link as LinkS, animateScroll as scroll } from 'react-scroll';
import './style.css';

const Navbar = () => {
  // State Values
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [scrollNav, setScrollNav] = useState(false);

  const changeNav = () => {
    if (window.scrollY >= 80) {
      setScrollNav(true);
    } else {
      setScrollNav(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', changeNav);
  }, []);

  const clickHandler = () => setClick(!click);

  const closeMobileMenu = () => setClick(false);

  const toggleHome = () => {
    scroll.scrollToTop();
  };

  const scrollContact = () => {
    scroll.scrollToBottom();
  };

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  console.log(scrollLinks[0].url);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container container'>
          <a
            href='/'
            className='navbar-logo'
            onClick={(closeMobileMenu, toggleHome)}
          >
            <DiCssdeck className='navbar-icon' />
            Cameron McCloskey
          </a>
          <div className='menu-icon' onClick={clickHandler}>
            {click ? <FaTimes /> : <FaBars />}
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <LinkS
                key='home'
                id='home'
                to='#'
                onClick={closeMobileMenu}
                smooth={true}
                duration={500}
                spy={true}
                exact='true'
                offset={-80}
                activeClass='active'
              >
                Home
              </LinkS>
            </li>
            <li className='nav-item'>
              <LinkS
                spy={true}
                exact='true'
                offset={-80}
                duration={500}
                key='about'
                id='about'
                to='about'
                className='nav-links'
                onClick={closeMobileMenu}
                activeClass='active'
              >
                About
              </LinkS>
            </li>
            <li className='nav-item'>
              <LinkS
                key='3'
                spy={true}
                exact='true'
                offset={-80}
                duration={500}
                key='technologies'
                id='technologies'
                to='technologies'
                className='nav-links'
                onClick={closeMobileMenu}
                activeClass='active'
              >
                Technologies
              </LinkS>
            </li>
            <li className='nav-item'>
              <LinkS
                key='portfolio'
                spy={true}
                exact='true'
                offset={-80}
                duration={500}
                id='portfolio'
                to='portfolio'
                className='nav-links'
                onClick={closeMobileMenu}
                activeClass='active'
              >
                Portfolio
              </LinkS>
            </li>
            <li>
              <LinkS
                key='5'
                smooth={true}
                id='footer'
                exact='true'
                offset={-80}
                duration={500}
                spy={true}
                onClick={(scrollContact, closeMobileMenu)}
                to='footer'
                className='btn-link'
                activeClass='active'
              >
                <button className='btn--outline'>Contact</button>
              </LinkS>
            </li>
          </ul>
        </div>
      </nav>
      {/* </IconContext.Provider> */}
    </>
  );
};

export default Navbar;
